mapper.update:
update game_data set id=#{id}
                   ,user_id=#{user_id}
                   ,username=#{username}
                   ,strength=#{strength}
                   ,fight_count=#{fight_count}
                   ,equipment=#{equipment}
                   ,money=#{money}
                   ,career=#{career}
                   ,equipped=#{equipped}
                   ,online_time=#{online_time} where user_id = #{user_id}
